mapper.select:
SELECT album_picture_id AS albumPictureId,user_id AS userId,picture_url AS pictureUrl, DATE_FORMAT(upload_time,'%Y-%m-%d %h:%m:%s')AS uploadTime,DATE_FORMAT(upload_time,'%Y-%m')AS timeGroup, size,type FROM album_picture
user_id= #{userId} AND DATE_FORMAT(upload_time,'%Y-%m')=DATE_FORMAT(date_sub(now(),interval #{monthNum} month),'%Y-%m')
ORDER BY upload_time DESC limit #{pictureNum}

select DATE_FORMAT(upload_time,'%Y-%m') as monthColum from album_picture

    user_id=#{userId}

group by monthColum

SELECT album_picture_id AS albumPictureId,picture_url AS pictureUrl FROM album_picture

    user_id=#{userId}

ORDER BY album_picture_id DESC limit #{num}
